import React from 'react';
import './style.css';
import useComponentState from './hooks/useComponentState';
import InputAdornment from '@mui/material/InputAdornment';
import { VideoCollection, Flex } from './components';
import { TextField, Box , Tab, Tabs } from '@mui/material';
import { Search, Sync } from '@mui/icons-material';
import { BrowserRouter, useParams } from "react-router-dom";


function VideoGrid (props) {

}


export default function App() {}


function useApp () {

  const queryParams = useParams()
  const { state, setState } = useComponentState({
    page: 1,
    param: '',
    collectionType: 'video',
    busy: false, 
    searches: []
  });
  const { page, param, collectionType, searches, busy } = state;

  const args = {
    setBusy: b => setState('busy', b),
    collectionType,
    searchParam: param,
    pageNum: page,
    onChange: (v) => setState('page', v),
  };

  const search = () => {
    setState('searches', searches.some(s => s.param === param) 
      ? searches.concat({
        param,
        type: 'search'
      }) 
      : searches.filter(s => s.param !== param))
    setState('page', 1);
    setState('collectionType', 'search');
  };

  const SearchIcon = busy ? Sync : Search;
  const iconClass = busy ? 'spin' : '';
  return {
    SearchIcon,
    iconClass,
    search,
    setState,
    args,
    ...state
  }
}
